      isFadeoutLeaves2: false,
      svg2: undefined
    }
  },
  watch: {
    pageWcNum() {
      this.draw(this.pageWcNum)
    }
  },
  methods: {
    draw(pageWcNum) {
      // console.log(pageWcNum, this.isDoingAnimation, this.isAnimatedSVG1)
