      var svg1PathTo = 'M360.57,1060.45c-11.17-.3-14.16,2.83-21-6s-16.49-25.74-24-34c-8.95-9.85-15-59.19-15-75-1.13-20.52,16.34-54,33-66s24-19,38-40.35c3.95-6,4.1-8.73,0-14.65-3.95-5.73-17.52-4.29-23,0-16.26,12.7-43.82,26.1-52.65,44.74-1.78,3.75-4.14,8.16-8.29,8.47-4,.29-6.92-3.4-9.27-6.6-21.19-28.87-55.18-49.1-91-49.72-2.92-.06-6.93,1.27-6.35,4.12a5.05,5.05,0,0,0,1.07,1.95l16.15,21.25c5,6.61,15.94,11.47,22.32,16.79,5.73,4.77,23.92,17.67,30,22,14.86,10.55,16,34,16,59,0,0,1.23,59.43,2.23,70.15,1.77,18.88,7.87,37.88,20.46,52.06,8.64,9.74,20.19,17.11,26.51,28.49Z'
      if (pageWcNum === 11) {
        if (!this.isDoingAnimation && !this.isAnimatedSVG1) {
          this.infAnim(this.svg1, svg1PathFrom, svg1PathMid, svg1PathTo, 'up', true)
          this.isDoingAnimation = true
          this.isAnimatedSVG1 = true
        }
      } else if (pageWcNum === 12) {
        if (!this.isDoingAnimation && this.isAnimatedSVG1) {
          this.infAnim(this.svg1, svg1PathFrom, svg1PathMid, svg1PathTo, 'down', true)
          this.isDoingAnimation = true
          this.isAnimatedSVG1 = false
        }
      }
      // Page2
      var svg2PathFrom = 'M290,1200c0-10,0-13,1-17a127.13,127.13,0,0,1,4-14c2-5,4-10,6-13,2-5,15-22,23-29,15.46-13.53,23.94-17.56,37.94-38.9,3.95-6,4.09-8.74,0-14.66-3.95-5.73-17.52-4.29-23,0-16.25,12.7-43.82,26.1-52.64,44.74-1.78,3.76-4.15,8.17-8.29,8.47-4,.29-6.92-3.4-9.27-6.6-21.19-28.87-55.18-49.09-91-49.72-2.91,0-6.92,1.27-6.34,4.12a5,5,0,0,0,1.07,2l16.14,21.24c5,6.61,15.94,11.47,22.32,16.79,5.73,4.77,24.82,16.63,30,22C255,1151,247,1142,253,1154c0,0-.61.56,2,11,1,4,1,7,1,13v22Z'
      var svg2PathTo = 'M298,1200v-76c0-14,1-18.19,1-34,21-14,38-19,58-27,24.53-9.81,7-30-5-28-12.82,2.14-46,19-51,23-4.69,3.75-2-12-1-27,1.73-26,2-26,2-36,0-4.15,0-34,1-45,.36-4-33.56-6-34-2-3,27-3,30-4,36-.48,2.87-3,18-3,22,0,.75-1.9,16.26-2,17-2,15-2.35,32.13-4,31-16-11-37-23-51-23-26,0-14.92,24.23-8,27,20,8,26,8,58,25,0,0-2.7,20-4,33-2,20-2,25-2,45v39Z'
      if (pageWcNum === 21) {
        this.isHideSVG1 = true
        this.isHideSVG2 = false
        this.isFadeoutLeaves2 = true
        this.infAnim(this.svg2, svg2PathFrom, null, svg2PathTo, 'up', false)
