        alert('cancel Page2')
      }
    },
    infAnim(el, pathFrom, pathMid, pathTo, dir, isFade) {
      // MEMO: thisがvueを指していないので対策
      var _this = this
      var mid, end, fadeinORfadeout
      if (dir === 'up') {
        mid = pathMid
        end = pathTo
        fadeinORfadeout = 'fadein'
        dir = 'down'
      } else {
        mid = pathMid
        end = pathFrom
        fadeinORfadeout = 'fadeout'
        dir = 'up'
      }
      if (fadeinORfadeout === 'fadeout' && isFade) {
        this.isFadeinLeaves = false
        this.isFadeoutLeaves = true
      }

      if (mid !== null) {
        el.animate({d: mid}, 1500, function() {
          el.animate({d: end}, 1000, function() {
            if (fadeinORfadeout === 'fadein' && isFade) {
              _this.isFadeinLeaves = true
              _this.isFadeoutLeaves = false
            }
            _this.isDoingAnimation = false
          })
        })
      } else {
        el.animate({d: end}, 1000, function() {
          if (fadeinORfadeout === 'fadein' && isFade) {
            _this.isFadeinLeaves = true
            _this.isFadeoutLeaves = false
          }
          _this.isDoingAnimation = false
        })
      }
    }
  },
