              this.isFadeoutLeaves4 = false
            }
          } else if (fadeinORfadeout === 'fadeout') {
            if (dataName === 'l3') {
              this.isFadeinLeaves3 = false
              this.isFadeoutLeaves3 = true
            } if (dataName === 'l4') {
              this.isFadeinLeaves4 = true
              this.isFadeoutLeaves4 = false
            }
          }
        }
      }
    }
  },
  mounted() {
    this.draw(this.pageWcNum)
    this.svg1 = window.Snap.select('#me_after_path')
