<script>
export default {
  props: {
    deg: {
      type: Number,
      default: 0
    }
  },
  data: function () {
    return {
      isShow_me1: false,
      isShow_me2: false,
      isShow_me3: false,
      isShow_leaf: false
    }
  },
  watch: {
    deg() {
      this.draw(this.deg)
    }
  },
  methods: {
    draw(deg) {
      console.log(deg)
      if (deg > 10) {
        this.isShow_me1 = true
      } else {
        this.isShow_me1 = false
      }
      if (deg > 20) {
        this.isShow_me2 = true
      } else {
        this.isShow_me2 = false
      }
      if (deg > 30) {
        this.isShow_me3 = true
      } else {
        this.isShow_me3 = false
      }
      if (deg > 40) {
        this.isShow_leaf = true
      } else {
        this.isShow_leaf = false
      }
    }
  },
  mounted() {
    this.draw(this.deg)
  }
}
</script>

